import React, { useState } from 'react';

export default function FeedbackForm({ apiBase, onAdded }) {
  const [studentName, setStudentName] = useState('');
  const [courseCode, setCourseCode] = useState('');
  const [comments, setComments] = useState('');
  const [rating, setRating] = useState(5);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!studentName.trim() || !courseCode.trim() || !rating) {
      setError('Please complete name, course code and rating.');
      return;
    }
    const payload = { studentName, courseCode, comments, rating: Number(rating) };
    try {
      const res = await fetch(`${apiBase}/feedback`, {
        method: 'POST',
        headers: {'Content-Type':'application/json'},
        body: JSON.stringify(payload)
      });
      if (!res.ok) {
        const err = await res.json();
        throw new Error(err.error || 'Failed to submit');
      }
      const data = await res.json();
      // fetch the created record (simple approach: re-fetch all)
      const listRes = await fetch(`${apiBase}/feedback`);
      const list = await listRes.json();
      onAdded(list[0]); // newest first
      setStudentName(''); setCourseCode(''); setComments(''); setRating(5);
    } catch (err) {
      console.error(err);
      setError(err.message || 'Submission failed');
    }
  };

  return (
    <div className="card">
      <h2>Submit Feedback</h2>
      {error && <div style={{color:'red'}}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Student Name</label><br/>
          <input value={studentName} onChange={e=>setStudentName(e.target.value)} />
        </div>
        <div>
          <label>Course Code</label><br/>
          <input value={courseCode} onChange={e=>setCourseCode(e.target.value)} />
        </div>
        <div>
          <label>Comments</label><br/>
          <textarea value={comments} onChange={e=>setComments(e.target.value)} rows={3} />
        </div>
        <div>
          <label>Rating (1-5)</label><br/>
          <select value={rating} onChange={e=>setRating(e.target.value)}>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
          </select>
        </div>
        <div style={{marginTop:10}}>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
}
